mapper.insert:
insert into goods (id, name, num, 
  price, createDate, updateDate,state
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{num,jdbcType=INTEGER}, 
  #{price,jdbcType=REAL}, #{createdate,jdbcType=TIMESTAMP}, #{updatedate,jdbcType=TIMESTAMP},#{state,jdbcType=INTEGER}
  )

mapper.select:
select * from goods where 1=1 and name like #{keyWord} and state=1

  ${where}


order by createDate DESC
